mapper.update:
UPDATE team SET 
	teamName = #{teamName}
	,locationIdx = #{locationIdx}
	,teamFavTime = #{teamFavTime}
	,teamFavNum = #{teamFavNum}
	,teamIntroduce = #{teamIntroduce}
WHERE teamIdx = #{teamIdx}

UPDATE photo SET 
	photoName = #{param1}
WHERE photoId = #{param2}

mapper.select:
SELECT 
	(SELECT l.gu FROM location l WHERE l.locationIdx = 
	(SELECT c.locationId FROM court c WHERE c.courtIdx = 
	(SELECT m.courtId FROM matching m))) AS gu
FROM matching m WHERE ORDER BY matchingIdx {param1}
	LIMIT 10 OFFSET #{offset};
